#liste des users
def combien_de_journalistes(array)
    l=array.length
    puts "le nombre de journalistes est #{l}!"
end


#nombre des handle qu contient  une chiffre
def nombre_handle_contient_numero(array)
    j=0
    array.each do |a|
        test=false
        for i in 0..9
            test=true if a.include? "#{i}"
        end
        j+=1 if test
    end
    puts "le nombre de handle de journalistes contient numero est #{j}"
end

#Nombre de handle qui contient Aude
def nombre_hande_contient_aude(array)
    j=0
    array.each do |a|
        j+=1 if a.downcase.match? 'aude'
    end
    puts "le nombre de handle de journalistes contient 'aude' est #{j}"
end


#Nombre de handle dont le pseudo commence par une Maj
def nombre_handle_commence_majuscule(array)
    j=0
    array.each do |a|
        minus=a[1]
        minus=minus.downcase
        j+=1 if minus!=a[1]
    end
    puts "le nombre de handle de journalistes commence par majuscule est #{j}"
end

#Nombre de handle dont le pseudo commence par un Min
def nombre_handle_contienne_majuscule(array)
    j=0
    array.each do |a|
        minus=a.downcase
        j+=1 if minus!=a
    end
    puts "le nombre de handle de journalistes contienne par majuscule est #{j}"
end


#Nombre de handle qui contient un "_"
def nombre_underscore(array)
    j=0
    array.each do |a|
        for i in 0..(a.length)
            j+=1 if a[i]=="_"
        end
    end
    puts "le nombre de '_' dans tous les pseudo est #{j}"
end


#Triage A-Z
def tri_par_ordre_alphabetique(array)
    b=array.sort
    puts b
end
def les_50_premier_courte_handle(array)
    ch=array
    ch=ch.sort{|x,y|x.length<=>y.length}
    ch=ch[0...50]
    puts ch
end 
def position_epenser(array)
    i=0
    while array[i]!="@epenser"
        i+=1
    end
    puts "@epenser se trouve à la #{i+1}éme position"
end


